exports.FormatDate = function(oridate,formatStr)   
{   
    var str = formatStr;   
    var Week = ['日','一','二','三','四','五','六'];  
  
    str=str.replace(/yyyy|YYYY/,oridate.getFullYear());   
    str=str.replace(/yy|YY/,(oridate.getYear() % 100)>9?(oridate.getYear() % 100).toString():'0' + (oridate.getYear() % 100));   
  
    str=str.replace(/MM/,oridate.getMonth()>9?oridate.getMonth().toString():'0' + oridate.getMonth());   
    str=str.replace(/M/g,oridate.getMonth());   
  
    str=str.replace(/w|W/g,Week[oridate.getDay()]);   
  
    str=str.replace(/dd|DD/,oridate.getDate()>9?oridate.getDate().toString():'0' + oridate.getDate());   
    str=str.replace(/d|D/g,oridate.getDate());   
  
    str=str.replace(/hh|HH/,oridate.getHours()>9?oridate.getHours().toString():'0' + oridate.getHours());   
    str=str.replace(/h|H/g,oridate.getHours());   
    str=str.replace(/mm/,oridate.getMinutes()>9?oridate.getMinutes().toString():'0' + oridate.getMinutes());   
    str=str.replace(/m/g,oridate.getMinutes());   
  
    str=str.replace(/ss|SS/,oridate.getSeconds()>9?oridate.getSeconds().toString():'0' + oridate.getSeconds());   
    str=str.replace(/s|S/g,oridate.getSeconds());   
  
    return str;   
};















